    def video_progress(self, stream, chunk, bytes_remaining):
            global video_previousprogress
            total_size = stream.filesize
            bytes_downloaded = total_size - bytes_remaining

            liveprogress = (int)(bytes_downloaded / total_size * 100)
            if liveprogress > video_previousprogress:
                    video_previousprogress = liveprogress
                    self.progressBar.setValue(liveprogress)
                    QApplication.processEvents()

    def muzik_progress(self, stream, chunk, bytes_remaining):
            global muzik_previousprogress
            total_size = stream.filesize
            bytes_downloaded = total_size - bytes_remaining

            liveprogress = (int)(bytes_downloaded / total_size * 100)
            if liveprogress > muzik_previousprogress:
                    muzik_previousprogress = liveprogress
                    self.progressBar_2.setValue(liveprogress)
                    QApplication.processEvents()

    def video_indir_btn(self):
            download_url = self.lineEdit.text()
            list = self.lineEdit_2.text().split("/")
            dosya_ismi = list.pop()
            dosya_yolu = ""
            for i in list:
                    dosya_yolu += i + "/"
            yt = YouTube(download_url)
            yt.register_on_progress_callback(self.video_progress)
            yt.streams.filter(only_audio=False).first().download(dosya_yolu, dosya_ismi)
            global video_previousprogress
            video_previousprogress = 0
            self.progressBar.setValue(0)
            self.lineEdit_2.setText("")
            self.lineEdit.setText("")
            self.msgbox = QtWidgets.QMessageBox()
            self.msgbox.setWindowIcon(QIcon("icons/icon.ico"))
            self.msgbox.addButton(QtWidgets.QPushButton('Tamam'), QtWidgets.QMessageBox.YesRole)
            self.msgbox.setIcon(QtWidgets.QMessageBox.Information)
            self.msgbox.setWindowTitle("Bilgilendirme")
            self.msgbox.setText(
                    "İndirme işlemi başarılı bir şekilde gerçekleştirilmiştir.")
            self.msgbox.show()

    def muzik_indir_btn(self):
            download_url = self.lineEdit_3.text()
            list = self.lineEdit_4.text().split("/")
            dosya_ismi = list.pop()
            dosya_yolu = ""
            for i in list:
                    dosya_yolu += i + "/"
            yt = YouTube(download_url)
            yt.register_on_progress_callback(self.muzik_progress)
            yt.streams.filter(only_audio=True).first().download(dosya_yolu, dosya_ismi)
            global muzik_previousprogress
            muzik_previousprogress = 0
            self.progressBar_2.setValue(0)
