    def percent_video(self, tem_video, total_video):
            perc_video = (float(tem_video) / float(total_video)) * float(100)
            return perc_video

    def percent_muzik(self, tem_muzik, total_muzik):
            perc_muzik = (float(tem_muzik) / float(total_muzik)) * float(100)
            return perc_muzik

    def minimize(self):
            self.showMinimized()

    def close(self):
            self.close()

    def mousePressEvent(self, event):  # +
            self.dragPos = event.globalPos()

    def mouseMoveEvent(self, event):  # !!!
            if event.buttons() == QtCore.Qt.LeftButton:
                    self.move(self.pos() + event.globalPos() - self.dragPos)
                    self.dragPos = event.globalPos()
                    event.accept()

