class MyWin(QtWidgets.QMainWindow, Ui_MainWindow):
        def __init__(self):
                super().__init__()
                self.setupUi(self)
                self.dragPos = QtCore.QPoint()  ##Pencere hareket ettirmek icin
                self.shadow = QGraphicsDropShadowEffect(self)
                self.shadow.setBlurRadius(20)
                self.shadow.setXOffset(0)
                self.shadow.setYOffset(0)
                self.shadow.setColor(QColor(0, 0, 0, 60))
                self.drop_shadow_frame.setGraphicsEffect(self.shadow)

        def mousePressEvent(self, event):  ##Pencere hareket ettirmek icin
                self.dragPos = event.globalPos()

        def mouseMoveEvent(self, event):  ##Pencere hareket ettirmek icin
                if event.buttons() == QtCore.Qt.LeftButton:
                        self.move(self.pos() + event.globalPos() - self.dragPos)
                        self.dragPos = event.globalPos()
                        event.accept()



